import plotly.graph_objects as go

# Define the integers and the square root of 2
integers = list(range(-5, 6))
sqrt_2 = 2**0.5

# Create a trace for the number line
trace = go.Scatter(
    x=integers,
    y=[0] * len(integers),
    mode='lines+markers',
    marker=dict(symbol='line-ns-open'),
    line=dict(width=2),
    text=integers,
)

# Create a trace for the square root of 2
sqrt_2_trace = go.Scatter(
    x=[sqrt_2],
    y=[0],
    mode='markers+text',
    marker=dict(symbol='x', size=10, color='red'),
    text=[f'Square root of 2 ({sqrt_2})'],
    textposition='top center',
)

# Create a layout for the figure
layout = go.Layout(
    xaxis=dict(
        title='Integers',
        showline=True,
        showgrid=False,
        zeroline=True,
    ),
    yaxis=dict(
        showticklabels=False,
    ),
    showlegend=False,
)

# Create the figure
fig = go.Figure(data=[trace, sqrt_2_trace], layout=layout)

# Show the figure
fig.show()
```

